SampleCode.New:

int m_Semaphore;
SimpleMutexLock m_Lockl
ConditionalVariable::Pointer m_Condition = ConditionalVariable::New();

m_Lock.Lock();
++m_Semaphore;
m_Condition->Signal();
m_Lock.Unlock();

m_Lock.Lock();
if ( m_Semaphore == 0 )
{
m_Condition->Wait( & td.m_Lock);
}
--m_Semaphore;
m_Lock.Unlock();
